import Menu from "./Navigation/Menu";
import Home from "./Screens/Home";
import "./App.css";




function App() {

  const width =  window.screen.width;
  console.log(width);

  let classNameCtrl = '';
  if (width < 1000) {
    classNameCtrl += 'display';
  }
  return (
    <div className="containerPage"> 
      <div className={classNameCtrl}>
      <Menu /></div>

      <Home/>

    </div>
  );
}

export default App;
